import React,{useState, useEffect} from "react";
import Editor from "./Editor";
import { Splitpanes, Pane } from "splitpanes";
import "splitpanes/dist/splitpanes.css";


function App() {

  const[html,setHtml]=useState("")
  const[css,setCss]=useState("")
  const[js,setJs]=useState("")
  const[srcDoc,setSrcDoc]=useState("")

  useEffect(()=>{
    const timeout=setTimeout(()=>{
        setSrcDoc(`
        <html>
          <body>${html}</body>
          <style>${css}</style>
          <script>${js}</script>
        </html>
      `)
    },250)

    return ()=>clearTimeout(timeout)

  },[html, css, js])

  return (
    <>
      <Splitpanes horizontal class="w-full h-full">
      <Pane min-size="20" class="border-b border-gray-700">
        <Splitpanes vertical>
            <Pane min-size="5">
              <Editor language="html" displayName="HTML" 
              value={html} onChange={setHtml}/>
            </Pane>
            <Pane min-size="5">
              <Editor language="css" displayName="CSS" 
              value={css} onChange={setCss}/>
            </Pane>
            <Pane min-size="5">
              <Editor language="javascript" displayName="JS" 
              value={js} onChange={setJs}/>
            </Pane>
        </Splitpanes>
      </Pane>
        <iframe
          srcDoc={srcDoc} 
          title="output"
          sandbox="allow-scripts"
          width="100%"
          height="100%"
        />
      </Splitpanes>
    </>
  );
}

export default App;
