import React,{useState} from 'react';
import {Editor as ControlledEditor} from '@monaco-editor/react';

export default function Editor(props) {
    const{
        displayName,
        value,
        language,
        onChange
    } = props

function handleChange(value){
    onChange(value)
}

const [open,setOpen]=useState(true)

  return (
    <div className= "editor-container">
        <div className='editor-title'>
            {displayName}
            <button
            onClick={(prevOPen)=>setOpen(()=>{
                if(prevOPen === true){
                    document.getElementsByClassName("editor-container").style.display="flex-grow:0";
                }
                else{
                    document.getElementsByClassName("editor-container").style.display="flex-grow:1";
                }
            })}
            >O/C</button>
        </div>
        <ControlledEditor 
        className={`code-mirror-wrapper${open ? `` : `collapsed`}`}
            theme='vs-dark'
            onChange={handleChange}
            defaultValue={value}
            defaultLanguage={language}
        />
    </div>
  )
}
