import React,{useState}  from 'react';
import {Editor as ControlledEditor} from '@monaco-editor/react';
import * as monaco from 'monaco-editor/esm/vs/editor/editor.api.js';


export default function Editor(props) {
    const{
        displayName,
        language,
        value,
        onChange
    } = props

function handleChange(value){
    onChange(value)
}

const [editor, setEditor] = useState(null);

function handleToggle() {
  if (editor.current) {
    const layoutInfo = editor.current.getLayoutInfo();
    layoutInfo.width = layoutInfo.width === 0 ? layoutInfo.originalWidth : 0;

    setTimeout(() => {
      editor.current.layout();
    }, 0);
  }
}

  return (
    <div className="editor-container" style={{ display: editor.current && editor.current.getLayoutInfo().width === 0 ? 'none' : 'flex' }}>
        <div className='editor-title'>
            {displayName}
            <button
            onClick={handleToggle}
            >o/c</button>
        </div>
        <ControlledEditor 
        className="code-mirror-wrapper"
            theme='vs-dark'
            onMount={setEditor}
            onChange={handleChange}
            defaultLanguage={language}
            defaultValue={value}
            options={{
                autoLayout:true,
          minimap: {
            enabled: false
          }
        }}

        />
    </div>
  )
}
